INSERT INTO manager_catalogitem VALUES (100009, 'Ultra 64GB SDXC Class 10 30 MB/s', 'SanDisk', 69.95, 39.95, 0.05, '<p><span style="color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px;">Get exactly the shots you want with the SanDisk Ultra 64GB SDXC Class 10 30 MB/s memory card. With up to 30 MB/s read/write speeds, you''ll see it takes more than a great digital SLR camera to bring your ideas to life. And with 64GB of space, you''ll have room to capture it all in RAW+JPEG, and save the editing for later.&nbsp;SanDisk engineered its Power Core Controller to take whatever your camera''s buffer can dish out. By distributing image data across the card more rapidly and efficiently, this card delivers professional performance.</span><br></p>', '<table class="data-table" id="product-attribute-specs-table" style="margin: 0px 0px 10px; padding: 0px; outline: none; border-collapse: separate; border-bottom-width: 0px; width: 614.1141357421875px; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px; background-color: rgb(255, 255, 255);"><colgroup style="margin: 0px; padding: 0px; outline: none;"><col width="25%" style="margin: 0px; padding: 0px; outline: none;"><col style="margin: 0px; padding: 0px; outline: none;"></colgroup><tbody style="margin: 0px; padding: 0px; outline: none;"><tr class="first odd" style="margin: 0px; padding: 0px; outline: none;"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Manufacturer</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">SanDisk</td></tr><tr class="even" style="margin: 0px; padding: 0px; outline: none; background-color: rgb(241, 241, 241);"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 5