INSERT INTO manager_catalogitem VALUES (100011, 'Reflector SunSilver/White M (80cm/32")', 'Profoto', 79.00, 39.00, 0.05, '<h2 style="margin-top: 0px; padding: 0px; outline: none; font-size: 2rem; color: rgb(37, 37, 37); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 2.2rem;">Details</h2><div class="std" style="margin: 0px; padding: 0px; outline: none; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px;"><p style="margin-bottom: 20px; padding: 0px; outline: none;">The&nbsp;<strong style="margin: 0px; padding: 0px; outline: none; color: rgb(37, 37, 37);">Profoto Reflector SunSilver/White M (80cm/32")</strong>&nbsp;is a collapsible reflector with both sunsilver and white sides. Use the sunsilver side to create a warmer light with a slightly higher output and more contrast. Use the white side to reflect light more naturally on your subjects.</p><ul style="margin-bottom: 0px; margin-left: 0px; outline: none; list-style: none;"><li style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">Shape sunlight or bounce flash light.</li><li style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">Ergonomically shaped handles make the screens easy to fold and hold in various positions.</li><li style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">Sturdy yet easily foldable metal frame.</li><li style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">High-quality fabrics.</li></ul></div>', '<h2 style="margin-top: 0px; padding: 0px; outline: none; font-size: 2rem; color: rgb(37, 37, 37); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 2.2rem;">Additional Information</h2><table class="data-table" id="product-attribute-specs-table" style="margin: 0px 0px 10px; padding: 0px; outline: none; border-collapse: separate; border-bottom-width: 0px; width: 614.1141357421875px; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px; background-color: rgb(255, 255, 255);"><colgroup style="margin: 0px; padding: 0px; outline: none;"><col width="25%" style="margin: 0px; padding: 0px; outline: none;"><col style="margin: 0px; padding: 0px; outline: none;"></colgroup><tbody style="margin: 0px; padding: 0px; outline: none;"><tr class="first odd" style="margin: 0px; padding: 0px; outline: none;"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Manufacturer</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">Profoto</td></tr><tr class="even" style="margin: 0px; padding: 0px; outline: none; background-color: rgb(241, 241, 241);"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Manufacturer SKU</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">100962</td></tr><tr class="last odd" style="margin: 0px; padding: 0px; outline: none;"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">UPC</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">734002753455</td></tr></tbody></table>', '3', 4, '3 Days', '2014-04-11', 99999, 15, true, '/static/catalog/images/products/profoto_4_reflectorsunsilverwhite_1.jpg', true, 0);
INSERT INTO manager_catalogitem VALUES (100012, '10.5mm f/2.8G ED-IF AF DX Fisheye Lens', 'Nikon', 774.95, 350.00, 0.05, '<h2 style="margin-top: 0px; padding: 0px; outline: none; font-size: 2rem; color: rgb(37, 37, 37); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 2.2rem;">Details</h2><div class="std" style="margin: 0px; padding: 0px; outline: none; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px;"><p style="margin-bottom: 20px; padding: 0px; outline: none;">The&nbsp;<strong style="margin: 0px; padding: 0px; outline: none; color: rgb(37, 37, 37);">Nikon 10.5mm f/2.8G ED-IF AF DX Fisheye Lens</strong>&nbsp;is an ultra-wide-angle, frame-filling fisheye, designed exclusively for use with Nikon’s DX-format, delivers incredible images with a 180-degree angle of view.</p><p style="margin-bottom: 20px; padding: 0px; outline: none;"><strong style="margin: 0px; padding: 0px; outline: none; color: rgb(37, 37, 37);">Fun and compact fisheye lens for DX-format cameras</strong><br style="margin: 0px; padding: 0px; outline: none;">This compact and lightweight fisheye lens makes anything you shoot more interesting. With its frame-filling 180° angle of view and unique bending effects, every scene and subject will take on new dimensions through the viewfinder. Achieve great low-light results with its fast f/2.8 maximum aperture, and get as close to your subjects as 1.2-in. (3-cm) with edge to edge sharpness in every shot.</p><p style="margin-bottom: 20px; padding: 0px; outline: none;"><strong style="margin: 0px; padding: 0px; outline: none; color: rgb(37, 37, 37);">Frame-filling 180° viewing angle</strong><br style="margin: 0px; padding: 0px; outline: none;">The AF DX Fisheye-NIKKOR 10.5mm f/2.8G ED achieves an ultra-wide picture angle of 180° on DX-format cameras while remaining compact and lightweight. Its unique fisheye bending effect with edge-to-edge sharpness opens up entirely new compositional possibilities. Try different viewpoints and angles to turn ordinary scenes into extraordinary photographs. And with a minimal focus distance of just 1.2-in. (3-cm), you can get an entirely new perspective on just about any subject.</p><p style="margin-bottom: 20px; padding: 0px; outline: none;"><strong style="margin: 0px; padding: 0px; outline: none; color: rgb(37, 37, 37);">Designed for creativity and performance</strong><br style="margin: 0px; padding: 0px; outline: none;">With a rounded diaphragm that makes out-of-focus elements appear more natural, a built-in flower-type lens hood and a 16mm equivalent focal length, the AF DX Fisheye-NIKKOR 10.5mm f/2.8G ED is optimized for DX-format Nikon D-SLR cameras. Its Extra-low Dispersion (ED) glass ensures superior sharpness and color, and Close-Range Correction (CRC) system provides high performance when taking pictures up close.</p></div>', '<table class="data-table" id="product-attribute-specs-table" style="margin: 0px 0px 10px; padding: 0px; outline: none; border-collapse: separate; border-bottom-width: 0px; width: 614.1141357421875px; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px; background-color: rgb(255, 255, 255);"><colgroup style="margin: 0px; padding: 0px; outline: none;"><col width="25%" style="margin: 0px; padding: 0px; outline: none;"><col style="margin: 0px; padding: 0px; outline: none;"></colgroup><tbody style="margin: 0px; padding: 0px; outline: none;"><tr class="first odd" style="margin: 0px; padding: 0px; outline: none;"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Manufacturer</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">Nikon</td></tr><tr class="even" style="margin: 0px; padding: 0px; outline: none; background-color: rgb(241, 241, 241);"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Manufacturer SKU</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">2148</td></tr><tr class="odd" style="margin: 0px; padding: 0px; outline: none;"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">UPC</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">018208021482</td></tr><tr class="even" style="margin: 0px; padding: 0px; outline: none; background-color: rgb(241, 241, 241);"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Lens Type</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">Fixed Focal Length</td></tr><tr class="odd" style="margin: 0px; padding: 0px; outline: none;"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Mount Type</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">DX</td></tr><tr class="even" style="margin: 0px; padding: 0px; outline: none; background-color: rgb(241, 241, 241);"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Aperture</th><td class="data last" style="margin: 0px; padding: 10px; outline: none;">f/2.8</td></tr><tr class="odd" style="margin: 0px; padding: 0px; outline: none;"><th class="label" style="margin: 0px; padding: 5px 10px; outline: none; color: rgb(54, 54, 54); border-color: rgb(255, 255, 255); background-color: rgb(247, 247, 247);">Focus Type</th><td class="d