INSERT INTO manager_catalogitem VALUES (100013, 'LED Kit Flolite 1X1', 'Chimera', 325.00, 100.00, 0.05, '<h2 style="margin-top: 0px; padding: 0px; outline: none; font-size: 2rem; color: rgb(37, 37, 37); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 2.2rem;">Details</h2><div class="std" style="margin: 0px; padding: 0px; outline: none; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px;"><p class="p1" style="margin-bottom: 20px; padding: 0px; outline: none;">The&nbsp;<strong style="margin: 0px; padding: 0px; outline: none; color: rgb(37, 37, 37);">Chimera LED Kit FloLight 1X1</strong>&nbsp;is lightweight, collapsible, easy to use, and long lasting. The Chimera Front Lens Screen blends the LEDs into one pleasing light source with a minimum loss of light output. Your unique Lightbank will enhance the quality and versality of your LED light. It is the perfect way to get the most out of your LED technology and still create soft, beautiful, diffused talent-friendly light.</p><p class="p1" style="margin-bottom: 20px; padding: 0px; outline: none;">The Chimera LED Kit FloLight 1X1 includes:</p><ul class="ul1" style="margin-bottom: 0px; margin-left: 0px; outline: none; list-style: none;"><li class="li4" style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">Lightbank with poles attached</li><li class="li4" style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">standard #LS30 Front Lens Screen</li><li class="li4" style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">Security Tab Velcro button</li><li class="li4" style="margin: 0px 0px 7px; padding: 0px; outline: none; list-style: disc inside;">Speed Ring Frame</li></ul></div>', '<h2 style="margin-top: 0px; padding: 0px; outline: none; font-size: 2rem; color: rgb(37, 37, 37); font-family: proxima-nova, ''Helvetica Neue''