INSERT INTO manager_catalogitem VALUES (100016, 'The Puffer Pop-Up Flash Diffuser', 'Gary Fong', 22.95, 10.00, 0.05, '<h2 style="margin-top: 0px; padding: 0px; outline: none; font-size: 2rem; color: rgb(37, 37, 37); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 2.2rem;">Details</h2><div class="std" style="margin: 0px; padding: 0px; outline: none; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px;"><p style="margin-bottom: 20px; padding: 0px; outline: none;">The&nbsp;<strong style="margin: 0px; padding: 0px; outline: none; color: rgb(37, 37, 37);">Gary Fong The Puffer Pop-Up Flash Diffuser</strong>&nbsp;is a compact flash diffuser that you mount directly in front of your camera''s hotshoe. The convex white diffuser softens the harsh light from your pop-up flash, creating an even glow that''s perfect for portraits or product shots. You can adjust the height of the Puffer, and it fits almost any camera with a pop-up flash and a hot shoe.</p><p style="margin-bottom: 20px; padding: 0px; outline: none;">NOTE: The Puffer is not compatible with the Canon EOS Rebel T4</p></div>', '<h2 style="margin-top: 0px; padding: 0px; outline: none; font-size: 2rem; color: rgb(37, 37, 37); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 2.2rem;">Additional Information</h2><table class="data-table" id="product-attribute-specs-table" style="margin: 0px 0px 10px; padding: 0px; outline: none; border-collapse: separate; border-bottom-width: 0px; width: 614.1141357421875px; color: rgb(85, 85, 85); font-family: proxima-nova, ''Helvetica Neue'', helvetica, arial, sans-serif; line-height: 29.42943000793457px; background-color: rgb(255, 255, 255);"><colgroup style="margin: 0px; padding: 0px; outline: none;"><col width="25%" style="margin: 0px; padding: 0px; outline: none;"><col style="margin: 0px; padding: 0px; outline: none;"></c